    /**
     * will close .litegraph.dialog
     * @returns void
     */
    checkPanels() {
        if (!this.canvas)
            return;
        var panels = this.canvas.parentNode.querySelectorAll(".litegraph.dialog");
        for (let i = 0; i < panels.length; ++i) {
            var panel = panels[i];
            if (!panel.node)
                continue;
            if (!panel.node.graph || panel.graph != this.graph)
                panel.close();
        }
    }

