function toString(a) {
    if (a && a.constructor === Object) {
        try {
            return JSON.stringify(a);
        } catch (err) {
            return String(a);
        }
    }
    return String(a);
}
LiteGraph.wrapFunctionAsNode("string/toString", toString, [""], "string");


