            subdivisions = Math.max(0, subdivisions) | 0;

            switch (type) {
                case 1: // CUBE:
                    this._mesh = GL.Mesh.cube({
                        size: size,
                        normals: true,
                        coords: true,
                    });
                    break;
                case 2: // PLANE:
                    this._mesh = GL.Mesh.plane({
                        size: size,
                        xz: true,
                        detail: subdivisions,
                        normals: true,
                        coords: true,
                    });
                    break;
                case 3: // CYLINDER:
                    this._mesh = GL.Mesh.cylinder({
                        size: size,
                        subdivisions: subdivisions,
                        normals: true,
                        coords: true,
                    });
                    break;
                case 4: // SPHERE:
                    this._mesh = GL.Mesh.sphere({
                        size: size,
                        long: subdivisions,
                        lat: subdivisions,
                        normals: true,
                        coords: true,
                    });
                    break;
                case 5: // CIRCLE:
                    this._mesh = GL.Mesh.circle({
                        size: size,
                        slices: subdivisions,
                        normals: true,
                        coords: true,
                    });
                    break;
                case 6: // HEMISPHERE:
                    this._mesh = GL.Mesh.sphere({
                        size: size,
                        long: subdivisions,
                        lat: subdivisions,
                        normals: true,
                        coords: true,
                        hemi: true,
                    });
                    break;
                case 7: // ICOSAHEDRON:
                    this._mesh = GL.Mesh.icosahedron({
                        size: size,
                        subdivisions: subdivisions,
                    });
                    break;
                case 8: // CONE:
                    this._mesh = GL.Mesh.cone({
                        radius: size,
                        height: size,
                        subdivisions: subdivisions,
                    });
                    break;
                case 9: // QUAD:
                    this._mesh = GL.Mesh.plane({
                        size: size,
                        xz: false,
                        detail: subdivisions,
                        normals: true,
                        coords: true,
                    });
                    break;
            }

            this.last_info.type = type;
            this.last_info.size = size;
            this.last_info.subdivisions = subdivisions;
            this._mesh.version = this.version++;
